import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios"

function Chart() {
    const [weeks, setWeeks] = useState([]);
    const [drNames, setDrNames] = useState([]);
    const [drPriceHistories, setDrPriceHistories] = useState([]);
    const [crNames, setCrNames] = useState([]);
    const [crPriceHistories, setCrPriceHistories] = useState([]);

    // useEffect(() => {
        var weeksTemp = [];
        var drNamesTemp = [];
        var drPriceHistoriesTemp = [];
        var crNamesTemp = [];
        var crPriceHistoriesTemp = [];

        axios
            .get("https://fantasy-api.formula1.com/partner_games/f1/players#")
            .then(res => {
                console.log(res);

                for(let i = 0; i < res.data.players.length; i++) {
                    if(res.data.players[i].position === "Driver") {
                        drNamesTemp = [...drNamesTemp, res.data.players[i].display_name];
                        //setDrNames(arr => [...arr, res.data.players[i].display_name]);
                    } else {
                        crNamesTemp = [...crNamesTemp, res.data.players[i].display_name];
                        //setCrNames(arr => [...arr, res.data.players[i].display_name]);
                    }

                    let seasonPrices = [];
                    for(let j = 0; j < res.data.players[i].season_prices.length; j++) {
                        seasonPrices.push(parseFloat(res.data.players[i].season_prices[j].price));
                    }
                    if(res.data.players[i].position === "Driver") {
                        drPriceHistoriesTemp = [...drPriceHistoriesTemp, seasonPrices];
                        //setDrPriceHistories(arr => [...arr, seasonPrices]);
                    } else {
                        crPriceHistoriesTemp = [...crPriceHistoriesTemp, seasonPrices];
                        //setCrPriceHistories(arr => [...arr, seasonPrices]);
                    }
                }

                console.log(drPriceHistoriesTemp[0].length)
                let numberOfWeeks = drPriceHistoriesTemp[0].length;
                for(let i = 1; i <= numberOfWeeks; i++) {
                    weeksTemp = [...weeksTemp, i]
                    //setWeeks(arr => [...arr, i]); 
                    // console.log(weeks)
                }

                setWeeks(weeksTemp);
                setDrNames(drNamesTemp);
                setDrPriceHistories(drPriceHistoriesTemp);
                setCrNames(crNamesTemp);
                setCrPriceHistories(crPriceHistoriesTemp);
            })
            .catch(err => {
                console.log(err);
            }) 
    // }, []);

    console.log(weeks, weeks.length);
    console.log(drNames, drNames.length);
    console.log(drPriceHistories, drPriceHistories.length);
    console.log(crNames, crNames.length);
    console.log(crPriceHistories, crPriceHistories.length);

    const data = {
        labels: weeks,
        datasets: [
            {
                label: drNames[0],
                data: drPriceHistories[0],
                fill: false,
                backgroundColor: "rgb(255, 99, 132)",
                borderColor: "rgba(255, 99, 132, 0.2)"
            }
        ]
    };

    const options = {
        scales: { 
            yAxes: [
                { 
                    ticks: {
                        beginAtZero: true,
                        maxTicksLimit: 5
                    }
                }
            ]
        }
    }

    return (
        <div>
            <h1>F1 Fantasy Prices</h1>
            <Line data={data} options={options} />
        </div>
    );
}

export default Chart;
