import React from "react";
import { Line } from "react-chartjs-2";
import axios from "axios"

function Chart() {
    let weekNumbers = []
    let contenderName = []
    let contenderPriceHistory = []

    axios
        .get("https://fantasy-api.formula1.com/partner_games/f1/players#")
        .then(res => {
            console.log(res);

            for(let i = 0; i < res.data.players.length; i++) {
                contenderName.push(res.data.players[i].display_name)

                let seasonPrices = [];
                for(let j = 0; j < res.data.players[i].season_prices.length; j++) {
                    seasonPrices.push(parseFloat(res.data.players[i].season_prices[j].price));
                }
                contenderPriceHistory.push(seasonPrices);
            }

            var numberOfWeeks = contenderPriceHistory.length;
            for(i = 1; i <= numberOfWeeks; i++) {
                weekNumbers.push(i);
            }
        })
        .catch(err => {
            console.log(err);
        })

        console.log(weekNumbers, contenderName, contenderPriceHistory)

    const data = {
        labels: ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"],
        datasets: [
            {
                label: "Daily High Temps",
                data: [69, 69, 69, 69, 70],
                fill: false,
                backgroundColor: "rgb(255, 99, 132)",
                borderColor: "rgba(255, 99, 132, 0.2)"
            }
        ]
    };

    const options = {
        scales: { 
            yAxes: [
                { 
                    ticks: {
                        beginAtZero: true,
                        maxTicksLimit: 5
                    }
                }
            ]
        }
    }

    return (
        <div>
            <h1>F1 Fantasy Prices</h1>
            <Line data={data} options={options} />
        </div>
    );
}

export default Chart;
