import React from "react";
import { Form, Button } from "react-bootstrap";


function DrPoll(props) {
    let lastNames = [];
    for (let i = 0; i < props.drivers.length; i++) {
        lastNames.push(props.drivers[i].slice(3));
    }

    return (
        <div>
            <div className="dr-poll">
                <form className="dr-form">
                    <button id={lastNames[0]} className="custom-btn first-btn" type="button" value={props.drivers[0]}>{props.drivers[0]}</button>
                    {(props.drivers.slice(1, props.drivers.length - 1)).map((name, index) => (
                        <button id={lastNames[index + 1]} className="custom-btn" type="button" value={name} style={{borderLeftColor: props.colors[index + 1]}}>{name}</button>
                    ))}
                    <button id={lastNames[19]} className="custom-btn last-btn" type="button" value={props.drivers[19]} style={{borderLeftColor: props.colors[19]}}>{props.drivers[19]}</button>
                </form>
                <label><Button varient="primary">Hi</Button></label>
            </div>
            <div className="dr-results">
            </div>
        </div>
    )
}

export default DrPoll;
